import React, { Component } from "react";
import PubSub from "pubsub-js";
// import axios from "axios";

export default class Search extends Component {
  search = async () => {
    PubSub.subscribe("sunday", { name: "tom", age: 18 });
    //获取用户的输入（连续解构赋值+重命名）
    const {
      keyWordElement: { value: keyword },
    } = this;
    //发送请求前，通知List更新状态
    /// this.props.updateAppState({isFirst:false, isLoading:true});///
    PubSub.publish("sunday", { isFirst: false, isLoading: true }); //消息发布，Search发布消息
    // Call the API to fetch users with the given name
    // axios.get(`http://localhost:3000/api1/search/users?q=${keyword}`).then(
    //   (response) => {
    //     // 请求成功后通知List更新状态
    //     ///this.props.updateAppState({isLoading:false,users:response.data.items})///
    //     PubSub.publish("sunday", {
    //       isLoading: false,
    //       users: response.data.items,
    //     });
    //   },
    //   (error) => {
    //     // 请求失败后通知List更新状态
    //     ///this.props.updateAppState({isLoading:false,error:error.message})///
    //     PubSub.publish("sunday", { isLoading: false, error: error.message });
    //   }
    // );

    //发送网络请求---使用fetch发送,未优化
    /*fetch(`http://localhost:3000/api1/search/users?q=${keyword}`)
     .then((response) => {
      console.log("connect with sever success");
       return response.json();
     },error=>{
      console.log("Connect with sever fel",error);
      return new Promise(()=>{})
     })
     .then(response=>{console.log("get data success",response)},error=>{console.log("get data fel",error)})
     .catch((error) => {
        PubSub.publish("sunday", { isLoading: false, error: error.message });
      });*/
    //发送网络请求---使用fetch（优化）
    try {
      const response = await fetch(
        `http://localhost:3000/api1/search/users?q=${keyword}`
      );
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.log("request get wrong", error);
    }
  };

  // Display the fetched users in the List component

  render() {
    return (
      <section className="jumbotron">
        <h3 className="jumbotron-heading">Search Github Users</h3>
        <div>
          <input
            ref={(c) => (this.keyWordElement = c)}
            type="text"
            placeholder="enter the name you search"
          />
          &nbsp;<button onClick={this.search}>Search</button>
        </div>
      </section>
    );
  }
}
