import React, { Component } from "react";
import PubSub from "pubsub-js";
import "./index.css";

export default class List extends Component {
  state = {
    users: [], //uesers初始值为数组
    ifFirst: true, //是否为第一次打开页面
    isLoading: false, //标识是否处于加载中
    error: "", //存储请求相关错误信息
  };

  componentDidMount() {
