    });
  }

  componentWillUnmount() {
    PubSub.unsubscribe(this.token); //取消消息订阅
  }
  render() {
    const { users, isFirst, isLoading, error } = this.state;
    return (
      <div className="row">
        {/* // 三元表达式可以连着写，注意这里是在jsx里面，不能使用if语句 */}
        {isFirst ? (
          <h2>Welcome!Please click to start search.</h2>
        ) : isLoading ? (
          <h2>Loading...</h2>
        ) : error ? (
          <h2 style={{ color: "red" }}>Error: {error}</h2> // 注意在jsx中，返回的JSX元素需要用{}来包��，这在jsx中是必需的，在JS中是可以不用{}的
        ) : (
          users.map((userObj) => {
            return (
              // 注意key要添加在map里面的最外层盒子这里，这个地方的id是唯一的，所以可以作为其id
              <div key={userObj.id} className="card">
                <a rel="noreferrer" href={userObj.html_url} target="_blank">
                  <img
                    alt="head_img"
                    src={userObj.avatar_url}
                    style={{ width: "100px" }}
                  />
                </a>
                <p className="card-text">{userObj.login}</p>
              </div>
            );
          })
        )}
      </div>
    );
  }
}
