import React, { Component } from "react";
import Search from "./components/Search";
import List from "./components/List";

export default class App extends Component {
  //初始化状态，uesers初始值为数组
  state = {
    users: [],
  };
  //定义一个函数
  saveUsers = (users) => {
    this.setState({ users }); //setState修改users
  };
  render() {
    const { users } = this.state; //解构，目的是为了拿到状态state里面的users。注意加this
    return (
      <div className="container">
        {/* 把这个函数写到儿子里面，然后去儿子组件里面接一下 */}
        <Search saveUsers={this.saveUsers} />
        <List users={users} /> {/* //由于前面解构了，所以这里直接拿users就行 */}
      </div>
    );
  }
}
